const url = new URL("http://localhost/api/admin/users");

let headers = {
    "Accept": "application/json",
    "Content-Type": "application/json",
}

let body = JSON.stringify({
    "name": "#ke&lt;)F}Zx.;;",
    "email": "RMY`F]BXVQ-O",
    "password": "?a[&gt;~%`fQBNr",
})

fetch(url, {
    method: "POST",
    headers: headers,
    body: body
})
    .then(response => response.json())
    .then(json => console.log(json));

